package p1;

public class P1_main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 11; i++) {
			System.out.println(i);
		}
		System.out.println();

		int x = 0;
		while (x <= 10) {
			x = x + 1;
			System.out.println(x);
		}
		System.out.println();

		int y = 0;
		do {
			y = y + 1;
			System.out.println(y);
		} while (y <= 10);
	}

}
